<div class="klient-classic" id="zkusenosti">
  <h3>{$nadpis}</h3>
  <p>
    "{$text}"
  </p>
  <div class="klient-bio">
    <div class="icon icon-middle">
      <i class="fa fa-user"></i>   
    </div>  
    <p><strong class="meno">{$meno_klienta}</strong></p>
    <div class="rating">
      <span class="glyphicon glyphicon-star"></span>
      <span class="glyphicon glyphicon-star"></span>
      <span class="glyphicon glyphicon-star"></span>
      <span class="glyphicon glyphicon-star"></span>
      <span class="glyphicon glyphicon-star"></span>
      <p>{$slovnik->hodnotenie}: 5/5</p>
    </div>
    <div class="clearfix"></div>
  </div>
  <a class="btn btn-medium main-btn" href="{$button_url}">{$button_text}</a> 
  <div class="clearfix"></div>
</div>                      
<!-- /klienti -->