<div class="social-btns">
  <div class="share-icons">
    <a class="facebook-icon" href="#" 
            onclick="
              window.open(
              'https://www.facebook.com/sharer/sharer.php?u={url}', 
              'facebook-share-dialog', 
              'width=626,height=436'); 
            return false;"><i class="fa fa-facebook"></i>
          </a>
    <a class="g-icon" href="https://plus.google.com/share?url={{url}}" onclick="javascript:window.open(this.href,
  '', 'menubar=no,toolbar=no,resizable=yes,scrollbars=yes,height=600,width=600');return false;"><i class="fa fa-google-plus"></i></a>
    <a href="#" class="twitter-icon" data-lang="sk" onclick="
    window.open(
      'https://twitter.com/share?url={url}',
      'width:400',
      'height:200');
    return false;"><i class="fa fa-twitter"></i>
    </a>
  </div>
</div>